import React from "react";
import { motion } from 'framer-motion';

const containerVariants = {
  hidden: {
    opacity: 0,
  },
  visible: {
    opacity: 1,
    transition: {
      delay: 1.5,
      duration: 1.5
    }
  },
  exit: {
    x: '-100vh',
    transition: {
      ease: 'easeInOut'
    }
  }
}

const nextVariants = {
  hidden: {
    x: '-100vw'
  },
  visible: {
    x: 0,
    transition: { type: 'spring', stiffness: 120 }
  }
}

const Home = () => {
  return (
    <div className="home-container">

      <motion.div className='welcome-quote'
        variants={nextVariants}
        initial='hidden'
        animate='visible'
      >
        <p className='quote-1'><em>"All problems in computer science can be solved by another level of indrection." <br/> - David Wheeler</em></p>
      </motion.div>

      <div className='welcome-quote-2'>
        <p className='quote-2'><em>"But that usually will create another problem." <br/> - David Wheeler</em></p>
      </div>

      <motion.div className='welcome-msg'
        variants={containerVariants}
        initial='hidden'
        animate='visible'
        exit='exit'
      >
        <p className='welcome'>Welcome and thanks for stopping by! My name is Ted Neben and I am a Fullstack Software Engineer.</p>
      </motion.div>

    </div>
  );
};

export default Home;
